/**
 * Views - View Factory
 * ===
 *
 * @module viewFactory
 */

////////////////////////////////////////////////////////////////////////////////
// Imports
////////////////////////////////////////////////////////////////////////////////
import LogService from '../../../src/services/log';
import AjaxService from '../../../src/services/ajax';

////////////////////////////////////////////////////////////////////////////////
// Definitions
////////////////////////////////////////////////////////////////////////////////
const API = 'http://localhost:3050';

////////////////////////////////////////////////////////////////////////////////
// Class
////////////////////////////////////////////////////////////////////////////////
/**
 * View
 * @class
 * @memberof module:views
 */
class ViewFactory {

  //////////////////////////////////////////////////////////////////////////////
  // Private Properties
  //////////////////////////////////////////////////////////////////////////////
  /**
   * @private
   * @type { module:logService.LogService }
   */
  _logService;

  /**
   * @private
   * @type { module:services.AjaxService }
   */
  _ajaxService;

  _templates;

  //////////////////////////////////////////////////////////////////////////////
  // Public Properties
  //////////////////////////////////////////////////////////////////////////////

  constructor() {
    this._logService = LogService.create(this.constructor.name);
    this._ajaxService = AjaxService.create();
    this._templates = {};
  }

  //////////////////////////////////////////////////////////////////////////////
  // Public Methods
  //////////////////////////////////////////////////////////////////////////////
  createView(options) {
    return this._ajaxService.get(options)
      .then((response) => {
        return response;
      }).catch((err) => {
        console.log(err);
      });

  }

  //////////////////////////////////////////////////////////////////////////////
  // Private Methods
  //////////////////////////////////////////////////////////////////////////////
  _loadTemplate() {

  }
  //////////////////////////////////////////////////////////////////////////////
  // Static Methods
  //////////////////////////////////////////////////////////////////////////////
  /**
   * Static factory method
   * @returns { module:views.ViewFactory }
   */
  static create() {
    return new ViewFactory();
  }
}

////////////////////////////////////////////////////////////////////////////////
// Exports
////////////////////////////////////////////////////////////////////////////////
export default ViewFactory;
